mapper.select:
SELECT COUNT(*) FROM ( SELECT S.* FROM ( SELECT FORMAT(@ROWNUM := @ROWNUM-1, 0) AS NUMROW ,BBSCTT_NO ,MBR_ID ,BBSCTT_TY ,BBSCTT_SJ ,BBSCTT_CN ,(CASE WHEN MDFR IS NULL THEN WRTER WHEN MDFR IS NOT NULL THEN MDFR END ) WRTER ,(CASE WHEN MDFCN_YMD IS NULL THEN DATE_FORMAT(WRT_YMD,'%Y.%m.%d') WHEN MDFCN_YMD IS NOT NULL THEN DATE_FORMAT(MDFCN_YMD,'%Y.%m.%d') END ) WRT_YMD ,DEL_YN ,VIEWS ,POST_FIX_YN ,(SELECT COUNT(BBSCTT_NO) FROM MP_FILE B WHERE A.BBSCTT_NO = B.BBSCTT_NO AND DEL_YN = "N") AS MP_FILE_CNT FROM BBS A , (select @ROWNUM:= (SELECT COUNT(*) FROM (SELECT (CASE WHEN MDFR IS NULL THEN WRTER WHEN MDFR IS NOT NULL THEN MDFR END ) WRTER ,(CASE WHEN MDFCN_YMD IS NULL THEN DATE_FORMAT(WRT_YMD,'%Y.%m.%d') WHEN MDFCN_YMD IS NOT NULL THEN DATE_FORMAT(MDFCN_YMD,'%Y.%m.%d') END ) WRT_YMD , BBSCTT_SJ , BBSCTT_CN FROM BBS WHERE 1=1 AND BBSCTT_TY='N02' AND DEL_YN = 'N' )AB WHERE 1=1
AND
DATE_FORMAT(WRT_YMD,'%Y.%m.%d') >= #{from}
AND
DATE_FORMAT(WRT_YMD,'%Y.%m.%d') <= #{to}
AND ( BBSCTT_SJ LIKE '%/${SearchStr}%' ESCAPE '/' OR BBSCTT_CN LIKE '%/${SearchStr}%' ESCAPE '/' )
AND BBSCTT_SJ LIKE '%/${SearchStr}%' ESCAPE '/'
AND BBSCTT_CN LIKE '%/${SearchStr}%' ESCAPE '/'
) +1) temp WHERE 1=1 AND BBSCTT_TY = 'N02' AND DEL_YN = "N" ORDER BY WRT_YMD DESC , BBSCTT_NO DESC )S WHERE 1=1
AND
DATE_FORMAT(WRT_YMD,'%Y.%m.%d') >= #{from}
AND
DATE_FORMAT(WRT_YMD,'%Y.%m.%d') <= #{to}
AND ( BBSCTT_SJ LIKE '%/${SearchStr}%' ESCAPE '/' OR BBSCTT_CN LIKE '%/${SearchStr}%' ESCAPE '/' )
AND BBSCTT_SJ LIKE '%/${SearchStr}%' ESCAPE '/'
AND BBSCTT_CN LIKE '%/${SearchStr}%' ESCAPE '/'
)D
